ency=1;L.TextXAlignment=Enum.TextXAlignment.Left;L.RichText=true;L.TextColor3=Color3.fromRGB(255,255,255);return L end local function TL(T) return Lbl(T,TSET.Font,TSET.Size)end local function DL(T) return Lbl(T,DSET.Font,DSET.Size)end local function FProp(O)local P={Text="TextTransparency",Fram="BackgroundTransparency",Imag="ImageTransparency"};local PR=P[string.sub(O.ClassName,1,4)]T:Create(O,TweenInfo.new(.25,STYLE,DIR),{[PR]=1}):Play()end local function STF(T,For)for _,v in next,T do if v==For then return true end end return false end local function FIDX(T,D)for i,O in next,T do if typeof(O)=="table"then if STF(O,D)then return i end else if O==D then return i end end end local function RST()for _,O in next,OBJS do O[2]=0;O[3]=false end end local function FO(O,S)wait(S)FProp(O)for _,D in next,O:GetDescendants()do FProp(D)end wait(.25) table.remove(OBJS,FIDX(OBJS,O))RST() end return{Notify=function(PROP) local PROP=typeof(PROP)=="table" and PROP or{} local T=PROP.Title local D=PROP.Description local DUR=PROP.Duration or 5;if T or D then local Y=T and 26 or 0;if D then local SZ=TS:GetTextSize(D,DSET.Size,DSET.Font,Vector2.new(0,0))for i=1,math.ceil(SZ.X/MAXW)do Y+=SZ.Y end;Y+=8 end local NL=R2();NL.Size=UDim2.new(1,0,0,Y);NL.Position=UDim2.new(1,0,0,Calc(CACHE).Y+PADDING*#CACHE);NL.AnchorPoint=Vector2.new(1,0) if T then local NT=TL(T);NT.Size=UDim2.new(1,-10,0,26);NT.Position=UDim2.fromOffset(10,0);NT.Parent=NL end if D then local ND=DL(D);ND.TextWrapped=true;ND.Size=UDim2.fromScale(1,1)+UDim2.fromOffset(-DESC_PADDING,T and -26 or 0);ND.Position=UDim2.fromOffset(10,T and 26 or 0);ND.TextYAlignment=Enum.TextYAlignment[T and "Top" or "Center"];ND.Parent=NL end S2().Parent=NL;NL.Parent=C;table.insert(OBJS,{NL,0,false});coroutine.wrap(FO)(NL,DUR)end end}
